#urlbar[open]>.urlbar-input-container[contextid]~.urlbarView>.urlbarView-body-outer>.urlbarView-body-inner {
  border-color: transparent !important;
}

#urlbar-background,
#urlbar {
  border-radius: 16px !important;
  background-color: #44128076 !important;
  border: 2px solid transparent !important;
}

#urlbar {
  background-image: linear-gradient(90deg, #9400FF, #ba00e5, #df06b9, #e62da7, #f13b95, #f64d86, #f85f7a, #fc7169, #f9845b, #e8ab52) !important;
}

#urlbar-background {
  margin-bottom: 2px !important;
